import"../css/pages/auth.css";import{user}from"./list";document.addEventListener("DOMContentLoaded",(()=>{document.querySelector(".auth-form").addEventListener("submit",(e=>{e.preventDefault();const t=document.getElementById("login").value,o=document.getElementById("password").value;if(user.login===t&&user.password===o){const e=Math.random().toString(36).substr(2);return localStorage.setItem("authToken",e),void(window.location.href="../todo")}alert("Неверные учетные данные")}))}));